Get-ChildItem -Path . -Recurse -File | Where-Object { $_.Extension -in '.py', '.yaml', '.yml', '.json', '.js', '.css', '.html', '.md', '.txt', '.env' -or $_.Name -eq '.gitignore' -or $_.Name -eq 'webpack.config.js' } | Sort-Object FullName | ForEach-Object { $relPath = $_.FullName.Substring((Get-Location).Path.Length + 1); Add-Content -Path "project_concatenated.txt" -Value "=== $relPath ==="; Add-Content -Path "project_concatenated.txt" -Value (Get-Content -Path $_.FullName -Raw); Add-Content -Path "project_concatenated.txt" -Value ""; Add-Content -Path "project_concatenated.txt" -Value "" }